import { useState, useEffect } from "react";
import { Container, Row, Col } from "react-bootstrap";

import PokemonCard from "./PokemonCard";

function PokemonGrid(pokemons, error) {
  const [pokeCards, setPokeCards] = useState();
  useEffect(() => {
    const myCards = pokemons.map((pok) => (
      <PokemonCard name={pok.name} image={pok.image} />
    ));
    setPokeCards(myCards);
  }, [pokemons]);

  return (
    <Container>
      <Row>
        <Col>{pokeCards[0]}</Col>
        <Col>{pokeCards[1]}</Col>
        <Col>{pokeCards[2]}</Col>
        <Col>{pokeCards[3]}</Col>
        <Col>{pokeCards[4]}</Col>
      </Row>
      <Row>
        <Col>{pokeCards[5]}</Col>
        <Col>{pokeCards[6]}</Col>
        <Col>{pokeCards[7]}</Col>
        <Col>{pokeCards[8]}</Col>
        <Col>{pokeCards[9]}</Col>
      </Row>
    </Container>
  );
}

export default PokemonGrid;
