import { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

import "./App.css";

import pokemons from "./db/pokemonNamesData";
import useFetchPokemon from "./hooks/useFetchPokemon";
import PokemonGrid from "./components/PokemonGrid";

function App() {
  const { res, error } = useFetchPokemon(pokemons);
  const [pokemonData, setPokemondata] = useState(null);
  const [originalArray, setOriginalArray] = useState(null);
  const [clickedPoke, setClickedPoke] = useState([]);

  useEffect(() => {
    if (!res) return;
    setPokemondata(res);
    setOriginalArray(res);
  }, [res]);

  useEffect(() => {
    console.log(clickedPoke);
  }, [clickedPoke]);

  function shuffleArray() {
    let arr = [...pokemonData];
    for (let i = arr.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [arr[i], arr[j]] = [arr[j], arr[i]];
    }
    setPokemondata(arr);
  }

  function pokeMatch() {
    console.log("match!");

    setClickedPoke((r) => {
      return [];
    });
  }

  function handleClick(pokemon) {
    setClickedPoke((arr) => {
      if (arr.includes(pokemon)) {
        pokeMatch();
        return arr;
      } else {
        shuffleArray();
        return [...arr, pokemon];
      }
    });
  }

  return error ? (
    <h1>Bro there is an error</h1>
  ) : (
    <div>
      <h1 style={{ textAlign: "center" }}>Lets Test Your Memmory</h1>
      {pokemonData !== null ? (
        <PokemonGrid pokemons={pokemonData} handleClick={handleClick} />
      ) : (
        <h1>...loading Game</h1>
      )}
      <button
        onClick={() => {
          shuffleArray();
        }}
      >
        shuffle
      </button>
    </div>
  );
}

export default App;
