import { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

import "./App.css";

import pokemons from "./db/pokemonNamesData";
import useFetchPokemon from "./hooks/useFetchPokemon";
import PokemonGrid from "./components/PokemonGrid";

function App() {
  const { res, error } = useFetchPokemon(pokemons);
  const [pokemonData, setPokemondata] = useState(null);

  function shuffleArray(array) {
    for (let i = array.length - 1; i > 0; i--) {
      const j = (Math.round(Math.random() * (i + 1))[(array[i], array[j])] = [
        array[j],
        array[i],
      ]);
    }
    return array;
  }

  console.log(res);
  useEffect(() => {
    if (!res) return;
    setPokemondata(res);
  }, [res]);

  return (
    <div>
      <h1 style={{ textAlign: "center" }}>Lets Test Your Memmory</h1>
      {pokemonData !== null ? (
        <PokemonGrid pokemons={pokemonData} />
      ) : (
        <h1>...loading Game</h1>
      )}
      <button onClick={() => setPokemondata(shuffleArray(pokemonData))}>
        shuffle
      </button>
    </div>
  );
}

export default App;
