import { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

import "./App.css";

import pokemons from "./db/pokemonNamesData";
import useFetchPokemon from "./hooks/useFetchPokemon";
import PokemonGrid from "./components/PokemonGrid";

function App() {
  const { res, error } = useFetchPokemon(pokemons);
  const [pokemonData, setPokemondata] = useState(null);

  console.log(pokemonData);

  function shuffleArray() {
    let arr = [...pokemonData];
    for (let i = arr.length - 1; i > 0; i--) {
      const j = Math.round(Math.random() * (i + 1));
      [arr[i], arr[j]] = [arr[j], arr[i]];
    }

    setPokemondata(arr);
  }

  // function shuffleArray() {
  //   let arr = [...pokemonData]; // Create a new array by copying pokemonData
  //   for (let i = arr.length - 1; i > 0; i--) {
  //     const j = Math.floor(Math.random() * (i + 1)); // Random index from 0 to i
  //     [arr[i], arr[j]] = [arr[j], arr[i]]; // Swap elements
  //   }

  //   setPokemondata(arr); // Update state with the new shuffled array
  // }

  useEffect(() => {
    if (!res) return;
    setPokemondata(res);
  }, [res]);

  return (
    <div>
      <h1 style={{ textAlign: "center" }}>Lets Test Your Memmory</h1>
      {pokemonData !== null ? (
        <PokemonGrid pokemons={pokemonData} />
      ) : (
        <h1>...loading Game</h1>
      )}
      <button
        onClick={() => {
          shuffleArray();
        }}
      >
        shuffle
      </button>
    </div>
  );
}

export default App;
